'use strict';

var program = require('commander');
var path = require('path');

var packageJson = require('../../package.json');
var develop = require('../dist/utils/develop');

var defaultHost = process.platform === 'win32' ? 'localhost' : '0.0.0.0';

program.version(packageJson.version).option('-h, --host <url>', 'Set host. Defaults to ' + defaultHost, defaultHost).option('-p, --port <port>', 'Set port. Defaults to 8000', '8000').parse(process.argv);

var directory = path.resolve('.');

program.directory = directory;

develop(program);